package com.dirolep.Utils;import com.dirolep.Domain.Person;import org.springframework.data.jpa.domain.Specification;import javax.persistence.criteria.Predicate;import java.util.ArrayList;import java.util.List;/** * * Created by xieyao on 2018/8/12. */public class SpecificationUtils {    public static Specification<Person> findPerson(String realNameOrPhoneOrCardNum){        return (root, query, cb) -> {            List<Predicate> predicate = new ArrayList<>();            if (null != realNameOrPhoneOrCardNum) {                predicate.add(cb.like(root.get("realName"), "%" + realNameOrPhoneOrCardNum + "%"));                predicate.add(cb.like(root.get("phone"), "%" + realNameOrPhoneOrCardNum + "%"));                predicate.add(cb.like(root.get("cardNum"), "%" + realNameOrPhoneOrCardNum + "%"));            }            Predicate[] array = new Predicate[predicate.size()];            return cb.or(predicate.toArray(array));        };    }}